export abstract class AbstractConvertedSchema {
    public readonly id: string;
    /* used to figure out which service file this schema is added to */
    public readonly tag: string;
    public readonly docs: string | undefined;
    /* if true the id is generated by fern, otherwise it is copied from openapi */
    public readonly generatedId: boolean;

    constructor({
        id,
        tag,
        docs,
        generatedId,
    }: {
        id: string;
        tag: string;
        generatedId: boolean;
        docs: string | undefined;
    }) {
        this.id = id;
        this.tag = tag;
        this.docs = docs;
        this.generatedId = generatedId;
    }
}
